function allocatValidator(){var worker=$('#workerlist').combobox('getValue');if(worker==""){$.messager.alert('提示','收运人员不能为空');return false}return true}function orderallocatform(){if(allocatValidator()){$.ajax({url:ws_url+'/rest/order/batchupdateorderWorker?token='+gtoken,contentType:'application/json;charset=UTF-8',type:'post',datatype:'json',data:'orderidlist='+$("#orderid_allocating").val()+'&worker='+$('#workerlist').combobox('getValue')+'&status=2',cache:false,success:function(data){console.log(data.status);console.log(data.message);$('#form2').form('clear');if(data.status=='success'){$.messager.alert('提示','修改成功！','info',function(){$('#orderAllocating').window('close');$('#ordergrid').datagrid("clearSelections");$('#ordergrid').datagrid("reload")})}else if(data.status=='failure'){$.messager.alert('提示','修改失败！','error',function(){$('#orderAllocating').window('close');$('#ordergrid').datagrid("clearSelections");$('#ordergrid').datagrid("reload")})}},error:function(xhr){console.log(xhr)}})}}